| laboratorio_id | uuid              | uuid     | YES         | null                          |
| status         | character varying | varchar  | NO          | 'RASCUNHO'::character varying |

-- üîü RESUMO EXECUTIVO
SELECT 
  'RESUMO GERAL' as tipo,
  (SELECT COUNT(*) FROM pedidos) as total_pedidos,
  (SELECT COUNT(*) FROM pedidos WHERE updated_at::date = CURRENT_DATE AND updated_at::time > '10:30:00') as modificados_apos_1030,
  (SELECT COUNT(*) FROM pedidos WHERE status IN ('PENDENTE', 'REGISTRADO', 'AG_PAGAMENTO', 'PAGO', 'PRODUCAO', 'PRONTO', 'ENVIADO', 'CHEGOU')) as pedidos_kanban_operacional,
  (SELECT COUNT(*) FROM pedidos WHERE laboratorio_id IS NULL) as sem_laboratorio,
  (SELECT COUNT(*) FROM pedidos WHERE status IS NULL) as sem_status;

